toi20_orbs.cpp<pre><code>#include &lt;bits/stdc++.h&gt;
using namespace std;

int main(){
    cin.tie(NULL)-&gt;sync_with_stdio(false);
    int n, t, l, r;
    cin &gt;&gt; n &gt;&gt; t &gt;&gt; l &gt;&gt; r;
    vector &lt;int&gt; v(n);
    for(int i = 0; i &lt; n; i++) cin &gt;&gt; v[i];
    sort(v.begin(), v.end());
    multiset&lt;int&gt; a, b, o;
    for(int i = 0; i &lt; l; i++){
        a.insert(v[i]);
    }
    for(int i = l; i &lt; r; i++){
        b.insert(v[i]);
    }
    for(int i = r; i &lt; n; i++){
        o.insert(v[i]);
    }

    while(t--){
        int x = *--a.end();
        int y = *--b.end();
        a.erase(--a.end());
        b.erase(--b.end());
        int new1 = y - x, new2 = (x + y) / 2;
        b.insert(new1);
        b.insert(new2);
        int firstB = *b.begin();
        b.erase(b.begin());
        a.insert(firstB);
        if (*--a.end() &gt; *b.begin()) {
            int temp1 = *--a.end(), temp2 = *b.begin();
            a.erase(--a.end());
            b.erase(b.begin());
            a.insert(temp2);
            b.insert(temp1);
        }
    }

    for(int i : a) cout &lt;&lt; i &lt;&lt; ' ';
    for(int i : b) cout &lt;&lt; i &lt;&lt; ' ';
    for(int i : o) cout &lt;&lt; i &lt;&lt; ' ';
}
