toi21_duty_free.cpp<pre><code>#include &lt;bits/stdc++.h&gt;

using namespace std;

const int N = 2e6 + 5;

int i, f, aux[N], par[N];

int dsu(int x){
    if (aux[x] &lt; f) {
        aux[x] = i;
        par[x] = x;
    }
    return par[x] = (par[x] == x ? x : dsu(par[x]));
}

int minimum_bag_rearrangement_time(vector&lt;int&gt; max_allowed_positions){
    int n  = max_allowed_positions.size(); 
    f = 1;
    iota(par, par + N, 0);
    memset(aux, -1, sizeof aux);
    int ans = 0;
    for (i = 1; i &lt;= n; i++) {
        int x = dsu(max_allowed_positions[i - 1]);
        if (x &lt;= 0) {
            ans++, f = i;
            x = dsu(max_allowed_positions[i - 1]);
        }
        par[x] = dsu(x - 1);
    }
    return ans;
}
