#define int long long
using namespace std;

int32_t main() {
    cin.tie(NULL)-&gt;sync_with_stdio(false);

    // input
    int n, m;
    cin &gt;&gt; n &gt;&gt; m;
    vector &lt;int&gt; a(n), ans(n);
    for (int i = 0; i &lt; n; i++) {
        cin &gt;&gt; a[i];
    }
    vector &lt;pair &lt;int, int&gt;&gt; mon(m);
    for (int i = 0; i &lt; m; i++) {
        cin &gt;&gt; mon[i].first &gt;&gt; mon[i].second; // P[i], C[i]
    }

    sort(mon.begin(), mon.end()); // เรียงช้อมูลตามค่า P

    vector &lt;int&gt; power(m);
    for (int i = 0; i &lt; m; i++) {
        power[i] = mon[i].first; // ดึงค่า P มาเก็บใน Array ใหม่
    }

    // Prefix Sum ของ C[i]
    vector &lt;int&gt; pref(m);
    pref[0] = mon[0].second;
    for (int i = 1; i &lt; m; i++) {
        pref[i] = pref[i - 1] + mon[i].second;
    }

    // หาคำตอบของฮีโร่แต่ละคนโดยการใช้ Binary Search
    for (int i = 0; i &lt; n; i++) {
        int idx = upper_bound(power.begin(), power.end(), a[i]) - power.begin() - 1; // หา index (ในที่นี้ ตั้งชื่อว่า idx)
        if (idx &lt; 0) continue; // idx &lt; 0 นั่นคือ ฮีโร่คนที่ i ไม่สามารถชนะมอนสเตอร์ตัวไหนได้เลย
        ans[i] = pref[idx]; // คำตอบของฮีโร่คนที่ i คือ pref[idx]
    }

    // Output
    for (int i = 0; i &lt; n; i++) {
        cout &lt;&lt; ans[i] &lt;&lt; "\n";
    }

    return 0;
}
</code></pre> <p>Total Time Complexity</p> <p>\(O((m + n) \log m)\)</p> <ul> <li>\(m \log m\) มาจากการเรียงพลังและเหรียญของมอนสเตอร์</li> <li>\(n \log m\) มาจากการคำนวณจำนวนเหรียญที่มากที่สุดที่ฮีโร่แต่ละคนสามารถได้จากการตีมอนสเตอร์ตอนท้าย</li> </ul>

