int32_t main() {
    // input
    int N,M;
    cin &gt;&gt; N &gt;&gt; M;
    vector&lt;string&gt; W(N),w;
    for (int i=0;i&lt;N;i++) {
        cin &gt;&gt; W[i];
    }
    int cnt=0;
    for (int i=0;i&lt;N;i++) {
        if (cnt==0) {
            cnt+=W[i].size();
        } else {
            cnt+=W[i].size()+1;
        }
        if (cnt&gt;M) {
            if (w.size()==1) {
                cout &lt;&lt; w[0];
                cnt=W[i].size();
                w.clear();
                w.push_back(W[i]);
            } else {
                int charcnt=0;
                for (auto j:w) {
                    charcnt+=j.size()+1;
                }
                charcnt-=1;
                int c=M-charcnt;
                int c_0=c%(w.size()-1);
                cnt=0;
                for (auto j:w) {
                    cout &lt;&lt; j;
                    if (cnt!=w.size()-1) {
                        for (int p=0;p&lt;c/(w.size()-1)+(cnt&lt;c_0)+1;p++) {
                            cout &lt;&lt; " ";
                        }
                    }
                    cnt++;
                }
                cnt=W[i].size();
                w.clear();
                w.push_back(W[i]);
            }
            cout &lt;&lt; '\n';
        } else {
            w.push_back(W[i]);
        }
    }
    if (!w.empty()) {
        for (int i=0;i&lt;w.size();i++) {
            cout &lt;&lt; w[i];
            if (i!=w.size()-1) {
                cout &lt;&lt; " ";
            }
        }
    }
}
