signed main() {
    cin.tie(nullptr)-&gt;sync_with_stdio(false);
    string s, ans = "";
    cin &gt;&gt; s;

    if (s.find('x') == string::npos) {
        cout &lt;&lt; 0 &lt;&lt; "\n";
        return 0;
    }

    int i = 0;
    while (i &lt; s.length()) {
        int sign = 1;
        if (s[i] == '+' || s[i] == '-') {
            if (s[i] == '-') sign = -1;
            i++;
        }

        string temp = "";
        while (i &lt; s.length() &amp;&amp; isdigit(s[i])) {
            temp += s[i];
            i++;
        }

        int coef = (temp.empty() ? 1 : stoi(temp)) * sign;
        int power = 0;

        if (i &lt; s.length() &amp;&amp; s[i] == 'x') {
            power = 1;
            i++;
            if (i &lt; s.length() &amp;&amp; s[i] == '^') {
                i++;
                string exp = "";
                while (i &lt; s.length() &amp;&amp; isdigit(s[i])) {
                    exp += s[i];
                    i++;
                }
                power = stoi(exp);
            }
        }

        if (power == 0) continue;

        int newCoef = coef * power;
        int newPow = power - 1;

        if (newCoef &gt; 0) ans += "+";
        ans += to_string(newCoef);

        if (newPow &gt; 0) {
            ans += "x";
            if (newPow &gt; 1) {
                ans += "^";
                ans += to_string(newPow);
            }
        }
    }

    if (!ans.empty() &amp;&amp; ans[0] == '+') ans.erase(ans.begin());
    if (ans.empty()) ans = "0";

    cout &lt;&lt; ans &lt;&lt; "\n";
    return 0;
}
